return items.flatMap(item => {
  const titulos = item.json.itens;

  if (!titulos || !Array.isArray(titulos)) {
    console.log('Nenhum título encontrado ou formato inválido.');
    return [{ json: { error: 'Nenhum título encontrado ou formato inválido.' } }];
  }

  const hoje = new Date();
  hoje.setHours(0, 0, 0, 0);

  const titulosParaAlerta = titulos.filter(titulo => {
    if (!titulo.vencimento) {
      console.log('Título sem data de vencimento:', titulo);
      return false;
    }

    const partesData = titulo.vencimento.split('/');
    const vencimento = new Date(partesData[2], partesData[1] - 1, partesData[0]);
    vencimento.setHours(0, 0, 0, 0);

    const diferencaEmMilissegundos = hoje.getTime() - vencimento.getTime();
    const diasAtraso = Math.floor(diferencaEmMilissegundos / (1000 * 60 * 60 * 24));

    console.log(`Título: ${titulo.numero}, Vencimento: ${titulo.vencimento}, Dias de Atraso: ${diasAtraso}`);

    return diasAtraso === 1 || diasAtraso === 18;
  });

  if (titulosParaAlerta.length === 0) {
    console.log('Nenhum título atendeu às condições de filtro (1 ou 18 dias de atraso).');
  }

  return titulosParaAlerta.map(titulo => {
    const partesData = titulo.vencimento.split('/');
    const vencimento = new Date(partesData[2], partesData[1] - 1, partesData[0]);
    vencimento.setHours(0, 0, 0, 0);

    const diferencaEmMilissegundos = hoje.getTime() - vencimento.getTime();
    const diasAtraso = Math.floor(diferencaEmMilissegundos / (1000 * 60 * 60 * 24));

    let statusAlerta = '';
    if (diasAtraso === 1) {
      statusAlerta = 'venceu_ontem';
    } else if (diasAtraso === 18) {
      statusAlerta = 'atraso_18_dias';
    }

    return {
      json: {
        ...titulo,
        statusAlerta: statusAlerta
      }
    };
  });
});

